import React, { Component } from 'react';
import '../main.css';
import { Link } from 'react-router-dom';
import {connect} from 'react-redux';
import { deleteItem, clearCart } from './actions';


class Basket extends Component {
    render() {
        if (this.props.arr_item.length >= 1) {
            return (
                <div className='view-container'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-md-9'>
                                <div className='table-responsive'>
                                    <table className='table-bordered table-striped table-condensed cf'>
                                        <tbody>
                                        {this.props.arr_item.map((item, index) => (
                                            <tr key={index}
                                                className='item-checout'>
                                                <td className='first-column-checkout'>
                                                    <img className='img-thumbnail ' src={item.image}
                                                         alt="Card image cap"/>
                                                </td>
                                                <td> {item.name}</td>
                                                <td>{item.price}</td>
                                                <td>{item.quantity}</td>
                                                <td>
                                                        <span className='delete-cart'
                                                              onClick={() => { this.props.deleteItem(item.id)}}/>
                                                </td>
                                            </tr>
                                        ))}
                                        </tbody>
                                    </table>
                                </div>
                                <div className='row'>
                                    <div className='pull-right total-user-checkout'>
                                        <b>Total:</b>
                                        {this.props.price}
                                    </div>
                                </div>
                            </div>
                            <div className='col-md-3 btn-user-checkout'>
                                <div>
                                    <Link   className='btn btn-info' to='/'>
                                        <span className='glyphicon glyphicon-info-sign'/>
                                        <span>Continue shopping!</span>
                                    </Link>
                                    <div>
                                        <button className='btn btn-danger' onClick={() => {
                                            this.props.clearCart()
                                        }}>
                                            <span className='glyphicon glyphicon--envelope'/>
                                            Clear cart
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )
        } else  {
            return  (
                <div className='view-container'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-md-9'>
                                <div>Your shopping cart is empty!</div>
                            </div>
                            <div className='col-md-3 btn-user-checkout'>
                                <Link to='/'>
                                    <div className='btn btn-info'>
                                        <span className='glyphicon glyphicon-info-sign'/>
                                        <span>Continue shopping!</span>
                                    </div>
                                </Link>
                                <div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )

        }
    }
}

function mapStateToProps(state) {
    return {
        arr_item: state.basket.arr_item,
        price: state.basket.price,
    }
}

function mapDispatchToProps(dispatch) {
    return {
        deleteItem: (id_del) => deleteItem(dispatch, id_del),
        clearCart: () => clearCart(dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket);
